.notification-frame {
    position: relative;
    margin-top: 250px;
    margin-bottom: 0px;
    left: 32px;
    cursor: pointer;
    width: 360px;
}

.notification-frame > .content {
    border-radius: 20px;
    box-shadow: rgb(220, 220, 220) 0px 0px 8px 0px;
    max-width: 360px;
    background-color: white;
    overflow-y: hidden;
}

.notification-frame > .content > .data {
    display: grid;
    grid-template-columns: 36px auto 28px;
    grid-template-rows: auto auto auto;
    grid-template-areas: "appIcon titlesAndBody timeAndThumbnail";
    gap: 0px 10px;
    padding: 12px 16px 12px 12px;
}

.notification-frame > .content > .data > .date {
    grid-area: timeAndThumbnail;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    gap: 10px;
}

.notification-frame > .content > .data > .date > div {
    text-align: right;
    font-size: 11px;
    line-height: 0.75;
}
